import React, { useState } from "react";
import {StyleSheet,View,Text,SafeAreaView,StatusBar,Button} from "react-native";

import Form from "./src/components/Form";
import colors from "./src/utils/colors";

export default function App() {
  const [num1, setNum1] = useState(0);
  const [num2, setNum2] = useState(0);
  const [suma, setSuma] = useState(null);
  const [resta, setResta] = useState(null);
  const [multi, setMulti] = useState(null);
  const [divi, setDivi] = useState(null);

 console.log(
    suma
  );
  console.log(resta);
  console.log(multi);
  console.log(divi);
  return (
    <>
      <StatusBar barStyle="light-content"></StatusBar>
      <SafeAreaView style={styles.safeArea}>
        <Text style={styles.titleApp}>Encabezado</Text>
        <Form setNum1={setNum1} setNum2={setNum2}></Form>
      </SafeAreaView>


      <SafeAreaView style={styles.safeResultArea}>
        <Text style={styles.text}>
          {suma == null ? "Suma:" : `Suma ${suma}`}
        </Text>
        <Text style={styles.text}>
          {resta == null
            ? "Resta:"
            : `Resta: ${resta}`}
        </Text>
        <Text style={styles.text}>
          {multi == null
            ? "Multiplicacion:"
            : `Multipliacion: ${multi}`}
        </Text>
        <Text style={styles.text}>
          {divi == null
            ? "Division:"
            : `Division: ${divi}`}
        </Text>
        <Button
        title="Enviar"
        style={styles.button}
        onPress={() => {
          setSuma(parseInt(num1) + parseInt(num2));
          setResta(parseInt(num1) - parseInt(num2));
          setDivi(parseInt(num1) / parseInt(num2));
          setMulti(parseInt(num1) * parseInt(num2));
        }}
      ></Button>
      </SafeAreaView>
    </>
  );
}

const styles = StyleSheet.create({
  safeArea: {
    backgroundColor: colors.PRIMARY_COLOR,
    height: 200,
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
    alignItems: "center",
    marginBottom: 120,
  },
  titleApp: {
    fontSize: 25,
    fontWeight: "bold",
    color: "#000",
    marginTop: 15,
  },
  button: {
    backgroundColor: colors.RESULTADOS_COLOR,
    marginLeft: 150,
    marginRight:150
  },
  text: {
    textAlign: "center",
    backgroundColor: "",
    fontWeight: "bold",
    fontSize: 18,
    marginBottom: 3,
  },
  safeResultArea: {
    backgroundColor: colors.RESULTADOS_COLOR,
    height: 400,
    borderRadius: 30,
    alignItems: "center",
    paddingTop: 20,
    marginTop: 50,
  },
  marginBottom: {
    marginBottom: 20,
  },
});
